import React, { useState } from "react";

const API_BASE_URL = "v1/space-marine";

const GetSpaceMarineList = () => {
    const [response, setResponse] = useState("");
    const [params, setParams] = useState({ sort: "id", order: "ASC", filter: "", page: 0, pageSize: 10 });

    const handleChange = (e) => {
        setParams({ ...params, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        let url = `${API_BASE_URL}?sort=${params.sort}&order=${params.order}&page=${params.page}&pageSize=${params.pageSize}`;
        if (params.filter.trim()) {
            url += `&filter=${encodeURIComponent(params.filter)}`;
        }
        fetch(url)
            .then((res) => res.text())
            .then((data) => setResponse(data))
            .catch((err) => setResponse(`错误：${err}`));
    };

    return (
        <section>
            <h2>Get SpaceMarine List</h2>
            <form onSubmit={handleSubmit}>
                <select name="sort" value={params.sort} onChange={handleChange}>
                    {['id', 'name', 'creationDate', 'health', 'heartCount', 'height'].map(option => (
                        <option key={option} value={option}>{option}</option>
                    ))}
                </select>
                <select name="order" value={params.order} onChange={handleChange}>
                    <option value="ASC">ASC</option>
                    <option value="DESC">DESC</option>
                </select>
                <input type="text" name="filter" value={params.filter} onChange={handleChange} placeholder="过滤条件" />
                <input type="number" name="page" value={params.page} onChange={handleChange} min="0" />
                <input type="number" name="pageSize" value={params.pageSize} onChange={handleChange} min="1" />
                <button type="submit">Get Data</button>
            </form>
            <pre className="response">{response}</pre>
        </section>
    );
};

const GetSpaceMarineById = () => {
    const [id, setId] = useState("");
    const [response, setResponse] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(`${API_BASE_URL}/${id}`)
            .then((res) => res.text())
            .then((data) => setResponse(data))
            .catch((err) => setResponse(`错误：${err}`));
    };

    return (
        <section>
            <h2>Get SpaceMarine by ID</h2>
            <form onSubmit={handleSubmit}>
                <input type="number" value={id} onChange={(e) => setId(e.target.value)} placeholder="输入 ID" min="1" />
                <button type="submit">Get Data</button>
            </form>
            <pre className="response">{response}</pre>
        </section>
    );
};

const PostSpaceMarine = () => {
    const [formData, setFormData] = useState({
        name: "", x: "", y: "", health: "", heartCount: "", height: "", meleeWeapon: "CHAIN_SWORD", chapterName: "", chapterWorld: ""
    });
    const [response, setResponse] = useState("");

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const xml = `
      <newSpaceMarine>
        <name>${formData.name}</name>
        <coordinates><x>${formData.x}</x><y>${formData.y}</y></coordinates>
        <health>${formData.health}</health>
        <heartCount>${formData.heartCount}</heartCount>
        <height>${formData.height}</height>
        <meleeWeapon>${formData.meleeWeapon}</meleeWeapon>
        <chapter><name>${formData.chapterName}</name><world>${formData.chapterWorld}</world></chapter>
      </newSpaceMarine>
    `;

        fetch(API_BASE_URL, { method: "POST", headers: { "Content-Type": "application/xml" }, body: xml })
            .then((res) => res.text())
            .then((data) => setResponse(data))
            .catch((err) => setResponse(`错误：${err}`));
    };

    return (
        <section>
            <h2>Add SpaceMarine</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
                <input type="number" name="x" value={formData.x} onChange={handleChange} placeholder="X (<=220)" required />
                <input type="number" name="y" value={formData.y} onChange={handleChange} placeholder="Y (<=288)" required />
                <input type="number" name="health" value={formData.health} onChange={handleChange} placeholder="Health (>=1)" required min="1" />
                <input type="number" name="heartCount" value={formData.heartCount} onChange={handleChange} placeholder="Heart Count (1-3)" required min="1" max="3" />
                <input type="number" name="height" value={formData.height} onChange={handleChange} placeholder="Height" required />
                <select name="meleeWeapon" value={formData.meleeWeapon} onChange={handleChange}>
                    {['CHAIN_SWORD', 'LIGHTING_CLAW', 'POWER_BLADE'].map(option => (
                        <option key={option} value={option}>{option}</option>
                    ))}
                </select>
                <input type="text" name="chapterName" value={formData.chapterName} onChange={handleChange} placeholder="Chapter Name" />
                <input type="text" name="chapterWorld" value={formData.chapterWorld} onChange={handleChange} placeholder="Chapter World" />
                <button type="submit">Add SpaceMarine</button>
            </form>
            <pre className="response">{response}</pre>
        </section>
    );
};

export function Main () {
    return (
        <div>
            <h1>SpaceMarine API Test Page</h1>
            <GetSpaceMarineList />
            <GetSpaceMarineById />
            <PostSpaceMarine />
        </div>
    );
};
